import React, { useState } from 'react';

const ChatBox = ({ messages, setMessages, connected, chatboxDimensions, setchatboxDimensions, sendMessage, handleConnect }) => {
  
    const [input, setInput] = useState('');
  



    


    return (
        <div>
            <div className="flex flex-col h-60 overflow-y-auto border border-gray-300 p-4 mb-4">
            {messages.map((msg, index) => (
                <div key={index} className="mb-2">
                <span className="font-bold">{`Client ${msg.clientId}: `}</span>
                {msg.message}
                </div>
            ))}
            </div>

            <div className="flex flex-col items-center">
            <div className="flex items-center space-x-4 mb-4">
                <input
                className="flex-grow border border-gray-300 p-2 rounded"
                type="text"
                value={input}
                onChange={(e) => setInput(e.target.value)}
                placeholder="Type your message..."
                />
                <button
                className={`bg-blue-500 text-white px-4 py-2 rounded ${
                    connected ? 'bg-red-500 hover:bg-red-600' : 'hover:bg-blue-600'
                }`}
                onClick={handleConnect}
                >
                {connected ? 'Disconnect' : 'Start Chat'}
                </button>
                {connected && (
                <button
                    className="bg-green-500 text-white px-4 py-2 rounded hover:bg-green-600"
                    onClick={sendMessage}
                >
                    Send
                </button>
                )}
            </div>
            </div>
        </div>
    )
}

export default ChatBox;
