import React, { useState, useEffect } from 'react';
import io from 'socket.io-client';

import 'tailwindcss/base.css';
import 'tailwindcss/components.css';
import 'tailwindcss/utilities.css';

const socket = io('http://localhost:5000'); // Replace with your backend URL

function App() {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');
  const [connected, setConnected] = useState(false);
  const [chatboxDimensions, setChatboxDimensions] = useState({
    width: 'w-96',
    height: 'h-55',
  });

  useEffect(() => {
    const handleReceivedMessage = (data) => {
      const { clientId, message } = data;
      console.log(`Received message from Client ${clientId}: ${message}`);
      setMessages((prevMessages) => [...prevMessages, { clientId, message }]);
    };

    const handleConnectedClients = (data) => {
      const clients = data.clients || [];
      setConnectedClients(clients);
    };

    socket.on('server_message', handleReceivedMessage);  // Change the event name to 'server_message'
    socket.on('connected_clients', handleConnectedClients);

    return () => {
      socket.off('server_message', handleReceivedMessage);  // Change the event name to 'server_message'
      socket.off('connected_clients', handleConnectedClients);
      socket.disconnect();
    };
  }, []);

  const handleConnect = () => {
    if (!connected) {
      socket.connect();
      setConnected(true);
      setChatboxDimensions({ width: 'w-116', height: 'h-106' });
      console.log(`Connected with Client ID: ${socket.id}`);
    } else {
      socket.disconnect();
      setConnected(false);
      setChatboxDimensions({ width: 'w-96', height: 'h-55' });
      console.log(`Disconnected from Client ID: ${socket.id}`);
      setMessages([]);
    }
  };

  const sendMessage = () => {
    if (input.trim() !== '') {
      const clientId = socket.id;
      socket.emit('client_message', { clientId, message: input });  // Change the event name to 'client_message'
      setInput('');
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gray-100">
      <div className={`bg-white p-8 rounded-lg shadow-md ${chatboxDimensions.width} ${chatboxDimensions.height}`}>
        <h1 className="text-4xl mb-4">Chat with Friends!</h1>

        <div className="flex flex-col h-60 overflow-y-auto border border-gray-300 p-4 mb-4">
          {messages.map((msg, index) => (
            <div key={index} className="mb-2">
              <span className="font-bold">{`Client ${msg.clientId}: `}</span>
              {msg.message}
            </div>
          ))}
        </div>


        <div className="flex flex-col items-center">
          <div className="flex items-center space-x-4 mb-4">
            <input
              className="flex-grow border border-gray-300 p-2 rounded"
              type="text"
              value={input}
              onChange={(e) => setInput(e.target.value)}
              placeholder="Type your message..."
            />
            <button
              className={`bg-blue-500 text-white px-4 py-2 rounded ${
                connected ? 'bg-red-500 hover:bg-red-600' : 'hover:bg-blue-600'
              }`}
              onClick={handleConnect}
            >
              {connected ? 'Disconnect' : 'Start Chat'}
            </button>
            {connected && (
              <button
                className="bg-green-500 text-white px-4 py-2 rounded hover:bg-green-600"
                onClick={sendMessage}
              >
                Send
              </button>
            )}
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
