import React, { useState, useEffect } from 'react';
import io from 'socket.io-client';

const socket = io('http://localhost:5000'); // Replace with your backend URL

function App() {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');
  const [connected, setConnected] = useState(false);

  useEffect(() => {
    socket.on('message', (data) => {
      const { clientId, message } = data;
      console.log(`Received message from Client ${clientId}: ${message}`);
      setMessages((prevMessages) => [...prevMessages, { clientId, message }]);
    });

    return () => {
      socket.disconnect();
    };
  }, []);

  const handleConnect = () => {
    if (!connected) {
      socket.connect();
      setConnected(true);
      console.log(`Connected with Client ID: ${socket.id}`);
    } else {
      socket.disconnect();
      setConnected(false);
      console.log(`Disconnected from Client ID: ${socket.id}`);
    }
  };

  const sendMessage = () => {
    if (input.trim() !== '') {
      const clientId = socket.id;
      socket.emit('message', { clientId, message: input });
      setInput('');
    }
  };

  return (
    <div>
      <div>
        <h1>Simple Chat App</h1>
        <div style={{ height: '300px', overflowY: 'scroll', border: '1px solid #ccc', padding: '10px' }}>
          {messages.map((msg, index) => (
            <div key={index}>{`Client ${msg.clientId}: ${msg.message}`}</div>
          ))}
        </div>
        <div>
          <button onClick={handleConnect}>
            {connected ? 'Disconnect' : 'Start Chat'}
          </button>
          {connected && (
            <>
              <input
                type="text"
                value={input}
                onChange={(e) => setInput(e.target.value)}
                placeholder="Type your message..."
              />
              <button onClick={sendMessage}>Send</button>
            </>
          )}
        </div>
      </div>
    </div>
  );
}

export default App;
