import React, { useState, useEffect } from 'react';
import io from 'socket.io-client';

import 'tailwindcss/base.css'; // Import Tailwind base styles
import 'tailwindcss/components.css'; // Import Tailwind components styles
import 'tailwindcss/utilities.css'; // Import Tailwind utilities styles

const socket = io('http://localhost:5000'); // Replace with your backend URL

function App() {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');
  const [connected, setConnected] = useState(false);

  useEffect(() => {
    const handleReceivedMessage = (data) => {
      const { clientId, message } = data;
      console.log(`Received message from Client ${clientId}: ${message}`);
      setMessages((prevMessages) => [...prevMessages, { clientId, message }]);
    };

    socket.on('message', handleReceivedMessage);

    return () => {
      socket.off('message', handleReceivedMessage);
      socket.disconnect();
    };
  }, []);

  const handleConnect = () => {
    if (!connected) {
      socket.connect();
      setConnected(true);
      console.log(`Connected with Client ID: ${socket.id}`);
    } else {
      socket.disconnect();
      setConnected(false);
      console.log(`Disconnected from Client ID: ${socket.id}`);
      setMessages([]); // Clear messages on disconnection
    }
  };

  const sendMessage = () => {
    if (input.trim() !== '') {
      const clientId = socket.id;
      socket.emit('message', { clientId, message: input });
      setInput('');
    }
  };

  return (
    <div className="min-h-screen bg-gray-100 flex items-center justify-center">
      <div className="bg-white p-8 rounded-lg shadow-md w-96">
        <h1 className="text-4xl mb-4">Simple Chat App</h1>

        <div className="max-h-60 overflow-y-auto border border-gray-300 p-2 mb-4">
          {messages.map((msg, index) => (
            <div key={index} className="mb-2">{`Client ${msg.clientId}: ${msg.message}`}</div>
          ))}
        </div>

        <div>
          <button
            className={`bg-blue-500 text-white px-4 py-2 rounded ${
              connected ? 'bg-red-500 hover:bg-red-600' : 'hover:bg-blue-600'
            }`}
            onClick={handleConnect}
          >
            {connected ? 'Disconnect' : 'Start Chat'}
          </button>
          {connected && (
            <>
              <input
                className="border border-gray-300 p-2 rounded ml-2"
                type="text"
                value={input}
                onChange={(e) => setInput(e.target.value)}
                placeholder="Type your message..."
              />
              <button
                className="bg-green-500 text-white px-4 py-2 rounded ml-2 hover:bg-green-600"
                onClick={sendMessage}
              >
                Send
              </button>
            </>
          )}
        </div>
      </div>
    </div>
  );
}

export default App;
