import React, { useState, useEffect } from 'react';
import io from 'socket.io-client';

const socket = io('http://localhost:5000'); // Replace with your backend URL

function App() {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');

  useEffect(() => {
    socket.on('message', (data) => {
      const { clientId, message } = data;
      console.log(`Received message from Client ${clientId}: ${message}`);
      setMessages((prevMessages) => [...prevMessages, { clientId, message }]);
    });

    return () => {
      socket.disconnect();
    };
  }, []); // Make sure to pass an empty dependency array to useEffect to run only once.

  const sendMessage = () => {
    if (input.trim() !== '') {
      const clientId = socket.id;
      socket.emit('message', { clientId, message: input });
      setInput('');
    }
  };  

  return (
    <div>
      <div>
        <h1>Simple Chat App</h1>
        <div style={{ height: '300px', overflowY: 'scroll', border: '1px solid #ccc', padding: '10px' }}>
          {messages.map((msg, index) => (
            <div key={index}>{`Client ${msg.clientId}: ${msg.message}`}</div>
          ))}
        </div>
        <div>
          <input
            type="text"
            value={input}
            onChange={(e) => setInput(e.target.value)}
            placeholder="Type your message..."
          />
          <button onClick={sendMessage}>Send</button>
        </div>
      </div>
    </div>
  );
}

export default App;
